import React, { useState } from "react";
import { Switch, Route, Router } from "react-router-dom";
import AuthForm from "./components/Auth/AuthForm";
import Main from "./components/Main";
import Layout from "./components/Layout/Layout";

function App() {
  return (
    <Switch>
      <Route exact path="/">
        <Main />
      </Route>
      <Layout>
      <Route path="/login">
        <AuthForm />
      </Route>
      <Switch>
        <Route path="/home" exact>
          <HomePage />
        </Route>
        {!authCtx.isLoggedIn && (
          <Route path="/auth">
            <AuthPage />
          </Route>
        )}
        {authCtx.isLoggedIn && (
          <Route path="/profile">
            <UserProfile />
          </Route>
        )}
        <Route path="*">
          <Redirect to="/" />
        </Route>
      </Switch>
      </Layout>
    </Switch>
  );
}

export default App;
