import React, { useState } from "react";
import { Switch, Route, Router } from "react-router-dom";
import Login from "./components/Auth/Login";
import Main from "./components/Main";
import "./App.css";

function App() {
  return (
    <Switch>
      <Route exact path="/">
        <Main />
      </Route>
      <div className="outer">
        <div className="inner">
          <Route path="/login">
            <Login />
          </Route>
        </div>
      </div>
    </Switch>
  );
}

export default App;
